(309, 1, '2017-10-15 02:44:49', '2017-10-15 01:44:49', '// define variables and set to empty values\r\n$nameErr = $emailErr = $genderErr = $websiteErr = "";\r\n$name = $email = $gender = $comment = $website = "";\r\n\r\nif ($_SERVER["REQUEST_METHOD"] == "POST") {\r\n  if (empty($_POST["name"])) {\r\n    $nameErr = "Name is required";\r\n  } else {\r\n    $name = test_input($_POST["name"]);\r\n    // check if name only contains letters and whitespace\r\n    if (!preg_match("/^[a-zA-Z ]*$/",$name)) {\r\n      $nameErr = "Only letters and white space allowed"; \r\n    }\r\n  }\r\n  \r\n  if (empty($_POST["email"])) {\r\n    $emailErr = "Email is required";\r\n  } else {\r\n    $email = test_input($_POST["email"]);\r\n    // check if e-mail address is well-formed\r\n    if (!filter_var($email, FILTER_VALIDATE_EMAIL)) {\r\n      $emailErr = "Invalid email format"; \r\n    }\r\n  }\r\n    \r\n  if (empty($_POST["website"])) {\r\n    $website = "";\r\n  } else {\r\n    $website = test_input($_POST["website"]);\r\n    // check if URL address syntax is valid (this regular expression also allows dashes in the URL)\r\n    if (!preg_match("/\\b(?:(?:https?|ftp):\\/\\/|www\\.)[-a-z0-9+&@#\\/%?=~_|!:,.;]*[-a-z0-9+&@#\\/%=~_|]/i",$website)) {\r\n      $websiteErr = "Invalid URL"; \r\n    }\r\n  }\r\n\r\n  if (empty($_POST["comment"])) {\r\n    $comment = "";\r\n  } else {\r\n    $comment = test_input($_POST["comment"]);\r\n  }\r\n\r\n  if (empty($_POST["gender"])) {\r\n    $genderErr = "Gender is required";\r\n  } else {\r\n    $gender = test_input($_POST["gender"]);\r\n  }\r\n}\r\n\r\nfunction test_input($data) {\r\n  $data = trim($data);\r\n  $data = stripslashes($data);\r\n  $data = htmlspecialchars($data);\r\n  return $data;\r\n}\r\n?>\r\n\r\n<h2>PHP Form Validation Example</h2>\r\n<p><span class="error">* required field.</span></p>\r\n<form method="post" action="<?php echo htmlspecialchars($_SERVER["PHP_SELF"]);?>">  \r\n  Name: <input type="text" name="name" value="<?php echo $name;?>">\r\n  <span class="error">* <?php echo $nameErr;?></span>\r\n  <br><br>\r\n  E-mail: <input type="text" name="email" value="<?php echo $email;?>">\r\n  <span class="error">* <?php echo $emailErr;?></span>\r\n  <br><br>\r\n  Website: <input type="text" name="website" value="<?php echo $website;?>">\r\n  <span class="error"><?php echo $websiteErr;?></span>\r\n  <br><br>\r\n  Comment: <textarea name="comment" rows="5" cols="40"><?php echo $comment;?></textarea>\r\n  <br><br>\r\n  Gender:\r\n  <input type="radio" name="gender" <?php if (isset($gender) && $gender=="female") echo "checked";?> value="female">Female\r\n  <input type="radio" name="gender" <?php if (isset($gender) && $gender=="male") echo "checked";?> value="male">Male\r\n  <span class="error">* <?php echo $genderErr;?></span>\r\n  <br><br>\r\n  <input type="submit" name="submit" value="Submit">  \r\n</form>\r\n\r\n<?php\r\necho "<h2>Your Input:</h2>";\r\necho $name;\r\necho "<br>";\r\necho $email;\r\necho "<br>";\r\necho $website;\r\necho "<br>";\r\necho $comment;\r\necho "<br>";\r\necho $gender;\r\n?>', 'Login/Register', '', 'inherit', 'closed', 'closed', '', '93-revision-v1', '', '', '2017-10-15 02:44:49', '2017-10-15 01:44:49', '', 93, 'http://stllovespets.com/2017/10/93-revision-v1', 0, 'revision', '', 0),
